			}		});		e.preventDefault();	//STOP default action	});	/* ==============================================	Ajax Submiting For Email Contact Form.	=====================================================================*/		$("#contact_form").submit(function(e)	{	$('#show_contact_msg').html('<div class=gen>Submiting..</div>');	var username = $('#contact_name').val();	var useremail = $('#contact_email').val();	var commenttext = $('#contact_text').val();		var formURL = $(this).attr("action");		var data = {					username:username,					useremail:useremail,					commenttext:commenttext,				   }		$.ajax(		{			url : formURL,			type: "POST",			data : data,					 success: function (res) {								 if(res=='1'){									$('#show_contact_msg').html('<div class=gen>Thank you very much, We will notify you when we lunch</div>');								 }													 if(res=='5'){								 $('#show_contact_msg').html('<div class=err>Please enter a valid email address</div>');								 }							}		});		e.preventDefault();	//STOP default action	});	</script>	<script type="text/javascript">	//Google maps	var map;	var marker;	var fmcLatLong = {lat: 40.792818 , lng: -81.353184 };	function initMap() {		map = new google.maps.Map(document.getElementById('map'), {			center: fmcLatLong,			zoom: 18,			zoomControl: true,		    zoomControlOptions: {		      style: google.maps.ZoomControlStyle.SMALL		    }		});		marker = new google.maps.Marker({			position: fmcLatLong,			title: 'First Mennonite Church',			map: map		});	}	</script>	<script src="https://maps.googleapis.com/maps/api/js?callback=initMap"        async defer></script>  </body></html>